#include "libwordle.h"

extern const uint8_t WORD_BINS[26*26];
extern const uint16_t WORD_LAST3[NUMWORDS];

char libwordle_isvalid(const char* word) {
  int i, j, n, prefix, suffix;
  // make sure it's exactly five letters A-Z
  for (i=0; i<5; i++) {
    char ch = word[i];
    if (ch < 'A' || ch > 'Z') return 0;
  }
  if (word[5] != '\0') return 0;
  // get prefix index
  prefix = LETTER(word[0]) * 26 + LETTER(word[1]);
  if (n = WORD_BINS[prefix]) {
    j = 0;
    for (i=0; i<prefix; i++) {
      j += WORD_BINS[i];
    }
    // get suffix code to match
    suffix = LETTER(word[2]) + (LETTER(word[3])<<5) + (LETTER(word[4])<<10);
    for (i=0; i<n; i++) {
      if (WORD_LAST3[j++] == suffix) return 1;
    }
  }
  return 0;
}

void libwordle_init(Wordle* state, const char* word) {
  memset(state, 0, sizeof(Wordle));
  memcpy(state->target, word, 5);
}

char libwordle_initindex(Wordle* state, int targetIndex) {
  int i, j, k;
  char word[5];
  j = 0;
  for (i=0; i<26*26; i++) {
    j += WORD_BINS[i];
    if (j > targetIndex) {
      word[0] = i / 26 + 'A';
      word[1] = i % 26 + 'A';
      k = WORD_LAST3[targetIndex];
      word[2] = ((k >> 0) & 31) + 'A';
      word[3] = ((k >> 5) & 31) + 'A';
      word[4] = ((k >> 10) & 31) + 'A';
      libwordle_init(state, word);
      return 1;
    }
  }
  return 0;
}

char libwordle_update(Wordle* state, const char* guess) {
  int i, j;
  if (!libwordle_isvalid(guess)) {
    return 0;
  }
  for (i=0; i<5; i++) {
    char result = GUESS_GREY;
    char letter = guess[i];
    if (letter == state->target[i]) {
      result = GUESS_GREEN;
    } else {
      for (j=0; j<5; j++)
        if (letter == state->target[j])
          result = GUESS_YELLOW;
    }
    state->guesses[i] = result;
    state->status[LETTER(letter)] |= 1 << result;
  }
  return 1;
}

